import './styles.css';
import React, { useState } from 'react';

const initialStats = {
  STR: 0,
  INT: 0,
  DED: 0,
  CHA: 0,
  AGI: 0
};

const armourOptions = [
  { value: 'none', label: 'None', armour: 0, agi: +2 },
  { value: 'light', label: 'Light', armour: 2, agi: 0 },
  { value: 'medium', label: 'Medium', armour: 4, agi: -2 },
  { value: 'heavy', label: 'Heavy', armour: 8, agi: -4 }
];

const defaultCharacter = {
  name: '',
  stats: { ...initialStats },
  primarySkill: '',
  secondarySkill: '',
  armourType: 'none',
  weaponLeft: '',
  weaponRight: '',
  upgradeLeft: 'None',
  upgradeRight: 'None',
  items: ['', '', ''],
  usedItems: [false, false, false],
  accessory: '',
  injuries: ['', '', '', '', ''],
  level: 0
};

export default function App() {
  const [characters, setCharacters] = useState(Array.from({ length: 5 }, () => ({ ...defaultCharacter })));
  const [activeIndex, setActiveIndex] = useState(0);
  const [teamName, setTeamName] = useState('');
  const [creditTotal, setCreditTotal] = useState(1000);

  const updateCharacter = (field, value) => {
    const updated = [...characters];
    updated[activeIndex][field] = value;
    setCharacters(updated);
  };

  const calculateCharacterCost = (character) => {
    // Placeholder: replace with proper cost calculation logic
    return character.level * 10;
  };

  const totalTeamCost = characters.reduce((sum, c) => sum + calculateCharacterCost(c), 0);
  const creditsUnspent = Math.max(creditTotal - totalTeamCost, 0);
  const overBudget = totalTeamCost > creditTotal;

  return (
    <div className="p-4 max-w-[500px] mx-auto space-y-6">
      {overBudget && (
        <div className="fixed top-1/2 left-1/2 z-50 -translate-x-1/2 -translate-y-1/2 pointer-events-none">
          <div className="text-6xl text-red-600 font-extrabold drop-shadow-lg">NOT ENOUGH CREDITS</div>
        </div>
      )}

      <div className="text-center">
        <h1 className="text-2xl font-bold">Team Overview</h1>
        <input
          type="text"
          value={teamName}
          onChange={(e) => setTeamName(e.target.value)}
          className="mt-2 p-2 border rounded w-full text-center"
          placeholder="Enter team name"
        />
      </div>

      <div className="space-y-2">
        {characters.map((char, index) => (
          <div key={index} className="border p-2 rounded flex justify-between items-center">
            <span>{char.name || `Character ${index + 1}`}</span>
            <span className="font-mono">{calculateCharacterCost(char)} cr</span>
          </div>
        ))}
      </div>

      <div className="flex justify-between items-center">
        <label className="flex-1 mr-2">
          Credit Total:
          <input
            type="number"
            max={9999}
            className="w-full border p-2 rounded mt-1"
            value={creditTotal}
            onChange={(e) => setCreditTotal(Math.min(9999, parseInt(e.target.value) || 0))}
          />
        </label>
        <div className="text-right">
          <div className="text-sm font-bold">Credits Unspent:</div>
          <div className="text-xl font-mono">{creditsUnspent}</div>
        </div>
      </div>
    </div>
  );
}
