import React, { useRef } from 'react';
import { useReactToPrint } from 'react-to-print';

function PrintableBox(props, ref) {
  return (
    <div ref={ref}>
      <h1>âœ… Hello Printer</h1>
      <p>This content should appear in your print preview.</p>
    </div>
  );
}

const ForwardedBox = React.forwardRef(PrintableBox);

export default function App() {
  const printRef = useRef();

  const handlePrint = useReactToPrint({
    content: () => printRef.current,
    documentTitle: 'Working-Print-Test',
  });

  return (
    <div>
      <h2>ðŸ§ª React-to-Print Minimal Test</h2>

      <ForwardedBox ref={printRef} />

      <button
        onClick={handlePrint}
        style={{
          marginTop: '2rem',
          padding: '1rem 2rem',
          fontSize: '1rem',
          backgroundColor: 'blue',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
        }}
      >
        Print Test
      </button>
    </div>
  );
}
