import React, { useState } from 'react';

const initialStats = {
  STR: 0,
  INT: 0,
  DED: 0,
  CHA: 0,
  AGI: 0
};

const armourOptions = [
  { value: 'none', label: 'None', armour: 0, agi: +2 },
  { value: 'light', label: 'Light', armour: 2, agi: 0 },
  { value: 'medium', label: 'Medium', armour: 4, agi: -2 },
  { value: 'heavy', label: 'Heavy', armour: 8, agi: -4 }
];

const skills = [
  { id: 1, name: 'None', description: '', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 2, name: '1.Smelly', description: 'Gain a free action to make a model, not engaged, within 4 inches do a dedication test. if failed, instantly move that model a full move directly away from this model. -4 charm.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: -4, AGI: 0 } },
  { id: 3, name: '2.Slippery', description: 'Model always succeeds on a Flee and has -4 to get hit in close combat. But due to bad friction,  -4 agility.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: -4 } },
  { id: 4, name: '3.Scared', description: 'Weak nerves but fast! -4 dedication and +4 agility.', modifiers: { STR: 0, INT: 0, DED: -4, CHA: 0, AGI: 4 } },
  { id: 5, name: '4.Thug', description: "Swelling bicepts but can't count tie your shoelaces. +4 strength and -4 intelligence.", modifiers: { STR: 4, INT: -4, DED: 0, CHA: 0, AGI: 0 } },
  { id: 6, name: '5.Vampire', description: 'Each time this model kills, get +2 on all stats. After third kill, model goes frenzy. In Frenzy it must always move, or run, and fight the closest model in melee. Resets at the end of the scenario.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 7, name: '6.Translucent', description: 'Other models get -4 to hit this model on ranged attacks. Also, this model always succeeds to hide. But due to trouble communicating, -8 charm.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: -8, AGI: 0 } },
  { id: 8, name: '7.Leather face', description: 'Lack of sunscreen has made this model’s face impenetrable. Cannot do a flirt action, but gain +1 armour.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 9, name: '8.Scissor hands', description: 'This model’s None/improvised attacks cannot be modified by opponents’ armour and power becomes 0 instead of normal penalty. But cannot equip or carry any weapon.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 10, name: '9.Swindler', description: 'Player always gains double the money after scenario. But all money can only be spent on this model, and nothing can ever be traded. +2 charm and +2 intelligence.', modifiers: { STR: 0, INT: 4, DED: 0, CHA: 4, AGI: 0 } },
  { id: 11, name: '10.Clumsy', description: 'When this skill is gained, roll immediately on the injury table. +4 charm.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 4, AGI: 0 } },
  { id: 12, name: '11.Brainwashed', description: 'This model has seen the light. Immune to convince action, -4 intelligence and +4 dedication.', modifiers: { STR: 0, INT: -4, DED: 4, CHA: 0, AGI: 0 } },
  { id: 13, name: '12.Bookworm', description: 'In theory, this model has seen many battles. +4 intelligence, but -2 to agility and -2 strength.', modifiers: { STR: -2, INT: 4, DED: 0, CHA: 0, AGI: -2 } },
  { id: 14, name: '13.Sticky', description: 'Model can run up terrain without any movement penalty, but can never remove equipped weapons.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 15, name: '14.Railed', description: 'Double movement! Must always move full in a straight line. If wanting to stop or turn, roll a dedication test. If failed, the model keeps going in the same direction and can only be stopped by structures or models over 2”.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 16, name: '15.Infiltrator', description: 'Model can choose to start the scenario on the opponent’s side, but cannot start base to base with anyone. Roll a charm test. On a fail, the model has been found out and killed before the scenario started. Roll on the injury table.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 17, name: '16.Zombie', description: 'If killed, roll injury but do not remove the model. It cannot activate the same round and gets play dead status. Then activates as normally the round after (Unless the injury roll was a 1). -8 on all stats.', modifiers: { STR: -8, INT: -8, DED: -8, CHA: -8, AGI: -8 } },
  { id: 18, name: '17.Bland', description: 'Utterly boring! This model’s stats are now all zero. This is your new base; delete your values set at creation. Additional modifiers still apply.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 19, name: '18.Radiant', description: 'Always succeed on flirt actions. Always fail on Hide actions. +4 charm.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 4, AGI: 0 } },
  { id: 20, name: '19.Enraged', description: 'No penalty to dual wielding. But -4 to succeed with all rolls except kill rolls and can never flee. +4 strength.', modifiers: { STR: 4, INT: 0, DED: 0, CHA: 0, AGI: 0 } }
];

const weapons = [
  { id: 1, name: 'None melee', description: '[Ded]  Power -8', modifiers: { AGI: 0 } },
  { id: 2, name: 'Light melee', description: '[Agi]  Power -2', modifiers: { AGI: 0 } },
  { id: 3, name: 'Medium melee', description: '[Str]  Power +2', modifiers: { AGI: -2 } },
  { id: 4, name: 'Heavy melee', description: '[Str]  Power +6', modifiers: { AGI: -4 } },
  { id: 5, name: 'None ranged', description: '[Ded]  Power -8  Range: 6”', modifiers: { AGI: 0 } },
  { id: 6, name: 'Short ranged', description: '[Agi]  Power -2  Range:12”', modifiers: { AGI: 0 } },
  { id: 7, name: 'Medium ranged', description: '[Agi]  Power +2  Range:18”', modifiers: { AGI: -2 } },
  { id: 8, name: 'Long ranged', description: '[Int]  power +4  Range:24”', modifiers: { AGI: -4 } },
  { id: 9, name: 'Artillery', description: '[Int]  power +6  Range:30”', modifiers: { AGI: -8 } }
];

const upgradeOptions = ['None', 'Fire', 'Lightning', 'Poison'];

const accessoryOptions = [
  { name: 'None', description: '', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Nice hat', description: 'Dashing and stylish, +4 charm. Lost permanently if leaping, falling or pushed.', modifiers: { STR: 4, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Ergonomic chair', description: 'Ugly but comfortable +4 intelligence, -4 agility and -4 charm. Cannot double move.', modifiers: { STR: -4, INT: -4, DED: 4, CHA: 0, AGI: 0 } },
  { name: 'Glass eye', description: 'Can detect hidden figures within 12 inches. But in return get bad depth perception and -4 to hit on all shoot and fight rolls.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 4, AGI: 0 } },
  { name: 'Cool tattoo', description: 'a +4 dedication, but the vanity makes all flirt actions automatically succeed on this character.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Big buckle', description: "a +4 strength, but -4 agility because you can't feel your legs.", modifiers: { STR: 0, INT: -4, DED: 0, CHA: 0, AGI: 4 } },
  { name: 'Stilts', description: 'model always move 3” in a straight line, regardless of move. but also counts all falls as extra +2”', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: '“New” underwear', description: 'a +1 dedication -1 charm. Sale now 75% reduced price. Only 10 credits!', modifiers: { STR: -1, INT: 0, DED: 0, CHA: 1, AGI: 0 } }
];


const itemOptions = [
  { name: 'None', description: '', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Yeasty drink', description: '[Ded] +4 armour, but -4 intelligence.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Smell well water', description: '[Cha] +4 on charm rolls, unable to get the hidden.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Rabid stew', description: '[Ded]  +4 strength, but -4 charm and can never flee.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Never-stop pill', description: '[Ded] +2 to all stats but roll more injury on model (see in depth)', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Teleporta bello', description: '[Int] teleport to any point seen within 20”. (see in depth)', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Detox', description: '[Int] Remove the poison condition.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Eek ink', description: '[agi] target must pass a [ded] or blindly run away (see in depth)', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { name: 'Airy Berry', description: '[Int] target becomes airborne until next activation (see in depth)', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } }
];


export default function App() {
  const [name, setName] = useState('');
  const [stats, setStats] = useState(initialStats);
  const [primarySkill, setPrimarySkill] = useState('');
  const [secondarySkill, setSecondarySkill] = useState('');
  const [notes, setNotes] = useState('');
  const [armourType, setArmourType] = useState('none');
  const [weaponLeft, setWeaponLeft] = useState('');
  const [weaponRight, setWeaponRight] = useState('');
  const [upgradeLeft, setUpgradeLeft] = useState('None');
  const [upgradeRight, setUpgradeRight] = useState('None');
  const [items, setItems] = useState(['', '', '']);
  const [accessory, setAccessory] = useState('');




  const selectedArmour = armourOptions.find(a => a.value === armourType);
  const getModifiers = () => {
    const primary = skills.find(s => s.id.toString() === primarySkill)?.modifiers || {};
    const secondary = skills.find(s => s.id.toString() === secondarySkill)?.modifiers || {};
    const weaponL = weapons.find(w => w.id.toString() === weaponLeft)?.modifiers || {};
    const weaponR = weapons.find(w => w.id.toString() === weaponRight)?.modifiers || {};
    const accMod = accessoryOptions.find(a => a.name === accessory)?.modifiers || {};
    const itemMods = items.map(i => itemOptions.find(opt => opt.name === i)?.modifiers || {});

    return Object.keys(initialStats).reduce((acc, key) => {
      const armourMod = key === 'AGI' ? selectedArmour?.agi || 0 : 0;
      const weaponMod = key === 'AGI' ? (weaponL.AGI || 0) + (weaponR.AGI || 0) : 0;
      const itemTotal = itemMods.reduce((sum, mod) => sum + (mod[key] || 0), 0);
      acc[key] = (primary[key] || 0) + (secondary[key] || 0) + armourMod + weaponMod + itemTotal + (accMod[key] || 0);
      return acc;
    }, {});
  };


  const updateStat = (key, value) => {
    const num = parseInt(value, 10);
    const clamped = Math.max(-10, Math.min(10, isNaN(num) ? 0 : num));
    setStats({ ...stats, [key]: clamped });
  };

  const getSkillDescription = (id) => {
    const skill = skills.find(s => s.id.toString() === id);
    return skill ? skill.description : '';
  };

  const calculateMove = () => 5 + Math.floor((stats.AGI + getModifiers().AGI) / 2);


  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Character Creator</h1>

      <div className="flex gap-4 items-end">
        <label className="flex-1">
          Name:
          <input
            className="block w-full border p-2 rounded mt-1"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <div className="flex-none">
          <div className="font-semibold">Move</div>
          <div className="p-2 border rounded text-center">{calculateMove()}</div>
        </div>
      </div>


      <div className="flex flex-wrap gap-4">
  {Object.keys(stats).map((key) => {
    const modifier = getModifiers()[key] || 0;
    return (
      <label key={key} className="flex-1 min-w-[100px]">
        {key}:
        <input
          type="number"
          className="block w-full border p-2 rounded mt-1"
          value={stats[key]}
          onChange={(e) => updateStat(key, e.target.value)}
          min="-10"
          max="10"
        />
        {modifier !== 0 && (
          <div className={`text-xs ${modifier > 0 ? 'text-green-600' : 'text-red-600'}`}>
            {modifier > 0 ? `+${modifier}` : modifier}
          </div>
        )}
      </label>
    );
  })}
</div>


      <h2 className="text-xl font-semibold mt-6 mb-2">Skills & Armour</h2>
      <div className="flex gap-4 items-start">
        <div className="flex-1">
          <div className="flex gap-4">
            <label className="flex-1">
              Primary Skill:
              <select
                className="block w-full border p-2 rounded mt-1"
                value={primarySkill}
                onChange={(e) => setPrimarySkill(e.target.value)}
              >
                <option value="">-- Select Skill --</option>
                {skills.map((skill) => (
                  <option key={skill.id} value={skill.id}>{skill.name}</option>
                ))}
              </select>
              {primarySkill && <p className="text-sm text-gray-600 mt-1">{getSkillDescription(primarySkill)}</p>}
            </label>

            <label className="flex-1">
              Secondary Skill:
              <select
                className="block w-full border p-2 rounded mt-1"
                value={secondarySkill}
                onChange={(e) => setSecondarySkill(e.target.value)}
              >
                <option value="">-- Select Skill --</option>
                {skills.map((skill) => (
                  <option key={skill.id} value={skill.id}>{skill.name}</option>
                ))}
              </select>
              {secondarySkill && <p className="text-sm text-gray-600 mt-1">{getSkillDescription(secondarySkill)}</p>}
            </label>
          </div>
        </div>

        <div className="w-1/5">
          <label className="block mb-2">
            Armour:
            <select
              className="block w-full border p-2 rounded mt-1"
              value={armourType}
              onChange={(e) => setArmourType(e.target.value)}
            >
              {armourOptions.map(opt => (
                <option key={opt.value} value={opt.value}>{opt.label}</option>
              ))}
            </select>
            <div className="text-sm text-center mt-1 font-bold">+{selectedArmour?.armour || 0}</div>
          </label>
        </div>
      </div>

      <h2 className="text-xl font-semibold mt-6 mb-2">Weapons</h2>
<div className="flex flex-col gap-6">
  {[
    ["Right", weaponRight, setWeaponRight, upgradeRight, setUpgradeRight],
    ["Left", weaponLeft, setWeaponLeft, upgradeLeft, setUpgradeLeft]
  ].map(([label, selected, setSelected, upgrade, setUpgrade], idx) => {
    const weapon = weapons.find(w => w.id.toString() === selected);
    return (
      <div key={idx}>
        {/* Header row: Weapon label + Upgrade label */}
        <div className="flex justify-between items-center mb-1">
          <span className="font-semibold">{label} Hand Weapon:</span>
          <span className="text-sm font-medium">Upgrade:</span>
        </div>

        {/* Input row: Weapon dropdown + description + upgrade dropdown */}
        <div className="flex gap-4 items-start">
          {/* Weapon dropdown */}
          <select
            className="w-48 border p-2 rounded"
            value={selected}
            onChange={(e) => setSelected(e.target.value)}
          >
            <option value="">-- Select Weapon --</option>
            {weapons.map((w) => (
              <option key={w.id} value={w.id}>{w.name}</option>
            ))}
          </select>

          {/* Description */}
          <div className="flex-1 text-sm text-gray-600 p-2">
            {weapon?.description || ''}
          </div>

          {/* Upgrade dropdown */}
          <select
            className="w-32 border p-2 rounded"
            value={upgrade}
            onChange={(e) => setUpgrade(e.target.value)}
          >
            {upgradeOptions.map(opt => (
              <option key={opt} value={opt}>{opt}</option>
            ))}
          </select>
        </div>
      </div>
    );
  })}
</div>
<h2 className="text-xl font-semibold mt-6 mb-2">Items & Accessory</h2>
<div className="flex gap-4">
  {/* Items container */}
  <div className="flex-1">
    <h3 className="font-semibold mb-2">Items</h3>
    {items.map((item, index) => {
      const selectedItem = itemOptions.find(opt => opt.name === item);
      return (
        <div key={index} className="flex gap-2 mb-2 items-start">
          <select
            className="w-48 border p-2 rounded"
            value={item}
            onChange={(e) => {
              const updated = [...items];
              updated[index] = e.target.value;
              setItems(updated);
            }}
          >
            {itemOptions.map((opt) => (
              <option key={opt.name} value={opt.name}>{opt.name}</option>
            ))}
          </select>
          <div className="flex-1 text-sm text-gray-600 p-2">{selectedItem?.description}</div>
        </div>
      );
    })}
  </div>

  {/* Accessory container */}
  <div className="w-1/2">
    <h3 className="font-semibold mb-2">Accessory</h3>
    <select
      className="w-full border p-2 rounded"
      value={accessory}
      onChange={(e) => setAccessory(e.target.value)}
    >
      {accessoryOptions.map((opt) => (
        <option key={opt.name} value={opt.name}>{opt.name}</option>
      ))}
    </select>
    <div className="text-sm text-gray-600 mt-2">
      {accessoryOptions.find(a => a.name === accessory)?.description}
    </div>
  </div>
</div>

      <label className="block mt-6 mb-2">
        Notes:
        <textarea
          className="block w-full border p-2 rounded mt-1"
          rows="4"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
        />
      </label>
    </div>
  );
}
