import React, { useState } from 'react';

const initialStats = {
  STR: 0,
  INT: 0,
  DED: 0,
  CHA: 0,
  AGI: 0
};

const armourOptions = [
  { value: 'none', label: 'None', armour: 0, agi: 2 },
  { value: 'light', label: 'Light', armour: 2, agi: 0 },
  { value: 'medium', label: 'Medium', armour: 4, agi: -2 },
  { value: 'heavy', label: 'Heavy', armour: 8, agi: -4 }
];

const skills = [
  // same as before
];

export default function App() {
  const [name, setName] = useState('');
  const [stats, setStats] = useState(initialStats);
  const [primarySkill, setPrimarySkill] = useState('');
  const [secondarySkill, setSecondarySkill] = useState('');
  const [notes, setNotes] = useState('');
  const [armourType, setArmourType] = useState('none');

  const selectedArmour = armourOptions.find(a => a.value === armourType);

  const updateStat = (key, value) => {
    const num = parseInt(value, 10);
    const clamped = Math.max(-10, Math.min(10, isNaN(num) ? 0 : num));
    setStats({ ...stats, [key]: clamped });
  };

  const getSkillDescription = (id) => {
    const skill = skills.find(s => s.id.toString() === id);
    return skill ? skill.description : '';
  };

  const calculateMove = () => 5 + Math.floor((stats.AGI + (selectedArmour?.agi || 0)) / 2);

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Character Creator</h1>

      <div className="flex gap-4 items-end">
        <label className="flex-1">
          Name:
          <input
            className="block w-full border p-2 rounded mt-1"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <div className="flex-none">
          <div className="font-semibold">Move</div>
          <div className="p-2 border rounded text-center">{calculateMove()}</div>
        </div>
      </div>

      <h2 className="text-xl font-semibold mt-6 mb-2">Stats</h2>
      <div className="flex flex-wrap gap-4">
        {Object.keys(stats).map((key) => {
          const modifier = key === 'AGI' ? (selectedArmour?.agi || 0) : 0;
          return (
            <label key={key} className="flex-1 min-w-[100px]">
              {key}:
              <input
                type="number"
                className="block w-full border p-2 rounded mt-1"
                value={stats[key]}
                onChange={(e) => updateStat(key, e.target.value)}
                min="-10"
                max="10"
              />
              {modifier !== 0 && (
                <div className="text-xs text-gray-500">{modifier > 0 ? `+${modifier}` : modifier}</div>
              )}
            </label>
          );
        })}
      </div>

      <h2 className="text-xl font-semibold mt-6 mb-2">Skills & Armour</h2>
      <div className="flex gap-4 items-start">
        <div className="flex-1">
          <div className="flex gap-4">
            <label className="flex-1">
              Primary Skill:
              <select
                className="block w-full border p-2 rounded mt-1"
                value={primarySkill}
                onChange={(e) => setPrimarySkill(e.target.value)}
              >
                <option value="">-- Select Skill --</option>
                {skills.map((skill) => (
                  <option key={skill.id} value={skill.id}>{skill.name}</option>
                ))}
              </select>
              {primarySkill && <p className="text-sm text-gray-600 mt-1">{getSkillDescription(primarySkill)}</p>}
            </label>

            <label className="flex-1">
              Secondary Skill:
              <select
                className="block w-full border p-2 rounded mt-1"
                value={secondarySkill}
                onChange={(e) => setSecondarySkill(e.target.value)}
              >
                <option value="">-- Select Skill --</option>
                {skills.map((skill) => (
                  <option key={skill.id} value={skill.id}>{skill.name}</option>
                ))}
              </select>
              {secondarySkill && <p className="text-sm text-gray-600 mt-1">{getSkillDescription(secondarySkill)}</p>}
            </label>
          </div>
        </div>

        <div className="w-1/5">
          <label className="block mb-2">
            Armour:
            <select
              className="block w-full border p-2 rounded mt-1"
              value={armourType}
              onChange={(e) => setArmourType(e.target.value)}
            >
              {armourOptions.map(opt => (
                <option key={opt.value} value={opt.value}>{opt.label}</option>
              ))}
            </select>
            <div className="text-sm text-center mt-1 font-bold">+{selectedArmour?.armour || 0}</div>
          </label>
        </div>
      </div>

      <label className="block mt-6 mb-2">
        Notes:
        <textarea
          className="block w-full border p-2 rounded mt-1"
          rows="4"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
        />
      </label>
    </div>
  );
}
