sourcesContent:
import './styles.css';
import React, { useState } from 'react';


const initialStats = {
  STR: 0,
  INT: 0,
  DED: 0,
  CHA: 0,
  AGI: 0
};


const armourOptions = [
  { value: 'none', label: 'None', armour: 0, agi: +2 },
  { value: 'light', label: 'Light', armour: 2, agi: 0 },
  { value: 'medium', label: 'Medium', armour: 4, agi: -2 },
  { value: 'heavy', label: 'Heavy', armour: 8, agi: -4 }
];

const skills = [
  { id: 1, name: 'None', description: '', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 2, name: '1.Smelly', description: 'Gain a free action to make a model, not engaged, within 4 inches do a dedication test. if failed, instantly move that model a full move directly away from this model. -4 charm.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: -4, AGI: 0 } },
  { id: 3, name: '2.Slippery', description: 'Model always succeeds on a Flee and has -4 to get hit in close combat. But due to bad friction,  -4 agility.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: -4 } },
  { id: 4, name: '3.Scared', description: 'Weak nerves but fast! -4 dedication and +4 agility.', modifiers: { STR: 0, INT: 0, DED: -4, CHA: 0, AGI: 4 } },
  { id: 5, name: '4.Thug', description: "Swelling bicepts but can't count tie your shoelaces. +4 strength and -4 intelligence.", modifiers: { STR: 4, INT: -4, DED: 0, CHA: 0, AGI: 0 } },
  { id: 6, name: '5.Vampire', description: 'Each time this model kills, get +2 on all stats. After third kill, model goes frenzy. In Frenzy it must always move, or run, and fight the closest model in melee. Resets at the end of the scenario.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 7, name: '6.Translucent', description: 'Other models get -4 to hit this model on ranged attacks. Also, this model always succeeds to hide. But due to trouble communicating, -8 charm.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: -8, AGI: 0 } },
  { id: 8, name: '7.Leather face', description: 'Lack of sunscreen has made this model’s face impenetrable. Cannot do a flirt action, but gain +1 armour.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 9, name: '8.Scissor hands', description: 'This model’s None/improvised attacks cannot be modified by opponents’ armour and power becomes 0 instead of normal penalty. But cannot equip or carry any weapon.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 10, name: '9.Swindler', description: 'Player always gains double the money after scenario. But all money can only be spent on this model, and nothing can ever be traded. +2 charm and +2 intelligence.', modifiers: { STR: 0, INT: 4, DED: 0, CHA: 4, AGI: 0 } },
  { id: 11, name: '10.Clumsy', description: 'When this skill is gained, roll immediately on the injury table. +4 charm.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 4, AGI: 0 } },
  { id: 12, name: '11.Brainwashed', description: 'This model has seen the light. Immune to convince action, -4 intelligence and +4 dedication.', modifiers: { STR: 0, INT: -4, DED: 4, CHA: 0, AGI: 0 } },
  { id: 13, name: '12.Bookworm', description: 'In theory, this model has seen many battles. +4 intelligence, but -2 to agility and -2 strength.', modifiers: { STR: -2, INT: 4, DED: 0, CHA: 0, AGI: -2 } },
  { id: 14, name: '13.Sticky', description: 'Model can run up terrain without any movement penalty, but can never remove equipped weapons.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
  { id: 15, name: '14.Railed', description: 'Double movement! Must always move full in a straight line. If wanting to stop or turn, roll a dedication test. If failed, the model keeps going in the same direction and can only be stopped by structures or models over 2”.', modifiers: { STR: 0, INT: 0, DED: 0, CHA: 0, AGI: 0 } },
