    return skill ? skill.description : '';
  };

const calculateMove = () => {
  const baseAGI = characters[activeCharacterIndex].stats.AGI || 0;
  const modAGI = getModifiers().AGI || 0;
  return 5 + Math.floor((baseAGI + modAGI) / 2);
};



