import React, { useRef } from 'react';
import { useReactToPrint } from 'react-to-print';

export default function App() {
  const printRef = useRef();

  const handlePrint = useReactToPrint({
    content: () => printRef.current,
    documentTitle: 'Test Print',
  });

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üñ®Ô∏è Print Demo</h1>

      {/* This box is what gets printed */}
      <div
        ref={printRef}
        style={{
          marginTop: '2rem',
          padding: '2rem',
          border: '2px dashed black',
          backgroundColor: 'white',
        }}
      >
        <h2>This is printable content</h2>
        <p>If you see this in your print preview, it worked üéâ</p>
      </div>

      {/* This button triggers the print */}
      <button
        onClick={handlePrint}
        style={{
          marginTop: '2rem',
          padding: '1rem 2rem',
          fontSize: '1rem',
          backgroundColor: 'green',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
        }}
      >
        Print Now
      </button>
    </div>
  );
}
