import React, { useState } from 'react';

const initialStats = {
  STR: 0,
  INT: 0,
  DED: 0,
  CHA: 0,
  AGI: 0
};

const armourOptions = [
  { value: 'none', label: 'None', armour: 0, agi: 2 },
  { value: 'light', label: 'Light', armour: 2, agi: 0 },
  { value: 'medium', label: 'Medium', armour: 4, agi: -2 },
  { value: 'heavy', label: 'Heavy', armour: 8, agi: -4 }
];

const skills = [
  { id: 1, name: '1.Smelly', description: 'Gain a free action to make a model, not engaged in melee, flee directly away from this model. (ded check).' },
  { id: 2, name: '2.Slippery', description: 'Model always succeeds on a Flee and has -4 to get hit in close combat. But due to bad friction, -4 agility.' },
  { id: 3, name: '3.Scared', description: 'Weak nerves but fast! -4 dedication and +4 agility.' },
  { id: 4, name: '4.Thug', description: "Swelling bicepts but can't count tie your shoelaces. +4 strength and -4 intelligence." },
  { id: 5, name: '5.Vampire', description: 'Each time this model kills, get +2 on all stats. After third kill, model goes frenzy. In Frenzy it must always move, or run, and fight the closest model in melee. Resets at the end of the scenario.' },
  { id: 6, name: '6.Translucent', description: 'Other models get -4 to hit this model on ranged attacks. Also, this model always succeeds to hide. But due to trouble communicating, -8 charm.' },
  { id: 7, name: '7.Leather face', description: 'Lack of sunscreen has made this model’s face impenetrable. Cannot do a flirt action, but gain +1 armour.' },
  { id: 8, name: '8.Scissor hands', description: 'This model’s None/improvised attacks cannot be modified by opponents’ armour and power becomes 0 instead of normal penalty. But cannot equip or carry any weapon.' },
  { id: 9, name: '9.Swindler', description: 'Player always gains double the money after scenario. But all money can only be spent on this model, and nothing can ever be traded. +2 charm and +2 intelligence.' },
  { id: 10, name: '10.Clumsy', description: 'When this skill is gained, roll immediately on the injury table. +4 charm.' },
  { id: 11, name: '11.Brainwashed', description: 'This model has seen the light. Immune to convince action, -4 intelligence and +4 dedication.' },
  { id: 12, name: '12.Bookworm', description: 'In theory, this model has seen many battles. +4 intelligence, but -2 to agility and -2 strength.' },
  { id: 13, name: '13.Sticky', description: 'Model can run up terrain without any movement penalty, but can never remove equipped weapons.' },
  { id: 14, name: '14.Railed', description: 'Double movement! Must always move full in a straight line. If wanting to stop or turn, roll a dedication test. If failed, the model keeps going in the same direction and can only be stopped by structures or models over 2”.' },
  { id: 15, name: '15.Infiltrator', description: 'Model can choose to start the scenario on the opponent’s side, but cannot start base to base with anyone. Roll a charm test. On a fail, the model has been found out and killed before the scenario started. Roll on the injury table.' },
  { id: 16, name: '16.Zombie', description: 'If killed, roll injury but do not remove the model. It cannot activate the same round and gets play dead status. Then activates as normally the round after (Unless the injury roll was a 1). -8 on all stats.' },
  { id: 17, name: '17.Bland', description: 'Utterly Boring! This model’s stats are now all zero, This is your new base, delete your values set at creation. Additional modifiers still apply.' },
  { id: 18, name: '18.Radiant', description: 'Always succeed on flirt actions, always fail on Hide actions. +4 charm.' },
  { id: 19, name: '19.Enraged', description: 'No penalty to dual wielding. But -4 to succeed with all rolls except kill rolls. And can never flee. +4 strength.' },
  { id: 20, name: '20.The legend', description: 'If this model ever rolls a crit or fumble, the character instantly disappears mysteriously (perma dead), before any action is carried out. +8 to all stats.' }
];

export default function App() {
  const [name, setName] = useState('');
  const [stats, setStats] = useState(initialStats);
  const [primarySkill, setPrimarySkill] = useState('');
  const [secondarySkill, setSecondarySkill] = useState('');
  const [notes, setNotes] = useState('');
  const [armourType, setArmourType] = useState('none');

  const selectedArmour = armourOptions.find(a => a.value === armourType);

  const updateStat = (key, value) => {
    const num = parseInt(value, 10);
    const clamped = Math.max(-10, Math.min(10, isNaN(num) ? 0 : num));
    setStats({ ...stats, [key]: clamped });
  };

  const getSkillDescription = (id) => {
    const skill = skills.find(s => s.id.toString() === id);
    return skill ? skill.description : '';
  };

  const calculateMove = () => 5 + Math.floor((stats.AGI + (selectedArmour?.agi || 0)) / 2);

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Character Creator</h1>

      <div className="flex gap-4 items-end">
        <label className="flex-1">
          Name:
          <input
            className="block w-full border p-2 rounded mt-1"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <div className="flex-none">
          <div className="font-semibold">Move</div>
          <div className="p-2 border rounded text-center">{calculateMove()}</div>
        </div>
      </div>

      <h2 className="text-xl font-semibold mt-6 mb-2">Stats</h2>
      <div className="flex flex-wrap gap-4">
        {Object.keys(stats).map((key) => {
          const modifier = key === 'AGI' ? (selectedArmour?.agi || 0) : 0;
          return (
            <label key={key} className="flex-1 min-w-[100px]">
              {key}:
              <input
                type="number"
                className="block w-full border p-2 rounded mt-1"
                value={stats[key]}
                onChange={(e) => updateStat(key, e.target.value)}
                min="-10"
                max="10"
              />
              {modifier !== 0 && (
                <div className="text-xs text-gray-500">{modifier > 0 ? `+${modifier}` : modifier}</div>
              )}
            </label>
          );
        })}
      </div>

      <h2 className="text-xl font-semibold mt-6 mb-2">Skills & Armour</h2>
      <div className="flex gap-4 items-start">
        <div className="flex-1">
          <div className="flex gap-4">
            <label className="flex-1">
              Primary Skill:
              <select
                className="block w-full border p-2 rounded mt-1"
                value={primarySkill}
                onChange={(e) => setPrimarySkill(e.target.value)}
              >
                <option value="">-- Select Skill --</option>
                {skills.map((skill) => (
                  <option key={skill.id} value={skill.id}>{skill.name}</option>
                ))}
              </select>
              {primarySkill && <p className="text-sm text-gray-600 mt-1">{getSkillDescription(primarySkill)}</p>}
            </label>

            <label className="flex-1">
              Secondary Skill:
              <select
                className="block w-full border p-2 rounded mt-1"
                value={secondarySkill}
                onChange={(e) => setSecondarySkill(e.target.value)}
              >
                <option value="">-- Select Skill --</option>
                {skills.map((skill) => (
                  <option key={skill.id} value={skill.id}>{skill.name}</option>
                ))}
              </select>
              {secondarySkill && <p className="text-sm text-gray-600 mt-1">{getSkillDescription(secondarySkill)}</p>}
            </label>
          </div>
        </div>

        <div className="w-1/5">
          <label className="block mb-2">
            Armour:
            <select
              className="block w-full border p-2 rounded mt-1"
              value={armourType}
              onChange={(e) => setArmourType(e.target.value)}
            >
              {armourOptions.map(opt => (
                <option key={opt.value} value={opt.value}>{opt.label}</option>
              ))}
            </select>
            <div className="text-sm text-center mt-1 font-bold">+{selectedArmour?.armour || 0}</div>
          </label>
        </div>
      </div>

      <label className="block mt-6 mb-2">
        Notes:
        <textarea
          className="block w-full border p-2 rounded mt-1"
          rows="4"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
        />
      </label>
    </div>
  );
}
